import { useEffect, useState } from "react";
import songsData from "./data/songs.json";
import Navbar from "./components/Navbar";
import SongCard from "./components/SongCard";
import Player from "./components/Player";
import './index.css';

export default function App() {
  const [search, setSearch] = useState("");
  const [currentSong, setCurrentSong] = useState(null);
  const [playing, setPlaying] = useState(false);
  const [theme, setTheme] = useState("dark"); // ðŸ‘ˆ NEW

  // Toggle between light/dark
  const toggleTheme = () => {
    const newTheme = theme === "dark" ? "light" : "dark";
    setTheme(newTheme);
  };

  // Apply body class based on theme
  useEffect(() => {
    document.body.className = theme;
  }, [theme]);

  const filteredSongs = songsData.filter(song =>
    song.title.toLowerCase().includes(search.toLowerCase()) ||
    song.artist.toLowerCase().includes(search.toLowerCase())
  );

  const playSong = (song) => {
    setCurrentSong(song);
    setPlaying(true);
  };

  return (
    <>
      <Navbar setSearch={setSearch} toggleTheme={toggleTheme} />
      <div className="grid">
        {filteredSongs.map(song => (
          <SongCard key={song.id} song={song} onPlay={playSong} />
        ))}
      </div>
      <Player currentSong={currentSong} playing={playing} />
      <footer>
        <p>ðŸŽ§ This is a Spotify-inspired music player made with React + pure CSS by Mujtaba Shabir.</p>
        <p>Explore your beats. Built for creativity and full marks. ðŸ’¯</p>
      </footer>
    </>
  );
}
